function sliding(tempo){if(old_tempo!=tempo&&typeof(jumper.length)!="undefined"){old_tempo=tempo;var currTime=parseInt(tempo);var i=0;var past_jumper_selector=new Array();while(i<jumper.length&&currTime>=parseInt(jumper[i]["tstart"])){past_jumper_selector[i]="#"+i;i++;}i--;if(i<jumper.length&&i!=jumper_old){jQuery("#"+jumper_old).css("background-color","#fff");jumper_old=i;var url=path_slide+"large/Slide"+(i+1)+".jpg";jQuery("#slide_src").attr("src",url);jQuery("#slide").fadeIn();if(prova<currTime){prova=currTime;}if(!stato){var set_stato=(i>=jumper.length-1)?1:0;jQuery.ajax({url:"index.php?option=com_gglms&task=updateTrack&secondi="+(currTime>prova?currTime:prova)+"&stato="+set_stato+"&id_elemento="+id_elemento,cache:false,success:function($data){if($data==0){alert("Impossibile effettuare il tracking del contenuto. Assicurati le tue impostazioni non blocchino le chiamate al server");}},error:function(){alert("Impossibile effettuare il tracking del contenuto. Assicurati le tue impostazioni non blocchino le chiamate al server");}});jQuery(past_jumper_selector.join(",")).css("background-color","#fff");}else{jQuery(".jumper").css("background-color","#fff");}jQuery("#"+i).css("background-color","#98ACC6");}}}